mapper.select:
		select * from(
                select
                    row_number() over(order by ficnic_no desc) rnum,
                    b.*
                from (
                select * from ficnic_list where ficnic_no > 0
				 and ficnic_category_no = #{category_no}
				 and ficnic_location like '%' || #{location} || '%' 
				 and ficnic_address like '%' || #{addr} || '%'
				 and ficnic_name like '%' || #{name} || '%'
		)b
		)

            where rnum >= #{startNo} and rnum <= #{endNo}
