mapper.select:
            select * from(
                select
                    row_number() over(order by reserv_date desc) rnum,
                    b.*
                from (
                    select * from reserv_list where reserv_no > 0
			         and reserv_status = #{search_status}
			         and reserv_date > to_date(#{search_date_start})
			         and reserv_date < to_date(#{search_date_end})
			         and reserv_sess like '%' || #{search_sess} || '%'
			         and reserv_ficnic_name like '%' || #{search_ficnic} || '%'
			         and reserv_name like '%' || #{search_name} || '%'
                ) b
            )

            where rnum >= #{startNo} and rnum <= #{endNo}
