mapper.select:
            select * from(
                select
                    row_number() over(order by reserv_date desc) rnum,
                    b.*
                from (
                    select * from reserv_list where reserv_no > 0
	       and reserv_status = #{search_type}
	       and reserv_date > to_date(#{startDate})
	       and reserv_date < to_date(#{endDate})
	       and reserv_sess like '%' || #{search_no} || '%'
	       and reserv_name like '%' || #{search_id} || '%'
	       and reserv_ficnic_name like '%' || #{search_name} || '%'
                ) b
            )

            where rnum >= #{startNo} and rnum <= #{endNo}
