mapper.select:
select count(*) from board_data_${bbs_id} where bdata_no > 0
and bdata_title like '%' || #{keyword} || '%' and bdata_cont like '%' || #{keyword} || '%' and bdata_writer_name like '%' || #{keyword} || '%'
and bdata_category = #{category}

select * from(
              select
                    row_number() over(order by bdata_use_notice desc, bdata_no desc) rnum,
                    b.*
              from (
                    select * from board_data_${bbs_id} where bdata_no > 0

                         and bdata_title like '%' || #{keyword} || '%'
                         and bdata_cont like '%' || #{keyword} || '%'
                         and bdata_writer_name like '%' || #{keyword} || '%'

                    and bdata_category = #{category}
              ) b
)

    where rnum >= #{startNo} and rnum <= #{endNo}
