mapper.select:
    select count(*) 
	from member_list
	where member_id = #{member_id}

    select count(*) 
	from member_list
	where member_id = #{member_id} and member_pw = #{member_pw}

    select count(*) 
	from member_list
	where member_email = #{member_email} and member_name = #{member_name}

    select count(*) 
	from member_list
	where member_email = #{member_email}

    select member_id
	from member_list
	where member_email = #{member_email} and member_name = #{member_name}

    select member_pw
	from member_list
	where member_email = #{member_email} and member_id = #{member_id}

    select member_pw
	from member_list
	where member_email = #{member_email} and member_id = #{member_id}
